import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage.js";
import { MarketService } from "./services/marketService.js";
import { NewsService } from "./services/newsService.js";
import { ChatService } from "./services/chatService.js";
import { CalculatorService } from "./services/calculatorService.js";
import { chatMessageSchema, calculatorInputSchema, taxCalculationSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // Initialize services
  const marketService = new MarketService();
  const newsService = new NewsService();
  const chatService = new ChatService();
  const calculatorService = new CalculatorService();

  // Initialize live market streams
  await marketService.initializeLiveStreams();

  // WebSocket server for real-time data
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  // Store active SSE connections
  const sseConnections = new Set<any>();

  // WebSocket connection handling
  wss.on('connection', (ws: WebSocket) => {
    console.log('WebSocket client connected');

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());

        if (data.type === 'subscribe') {
          // Handle subscription to market data streams
          console.log('Client subscribed to:', data.symbols, data.kind);
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      console.log('WebSocket client disconnected');
    });
  });

  // Broadcast market updates to WebSocket clients
  function broadcastMarketUpdate(data: any) {
    wss.clients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(JSON.stringify({
          type: 'market_update',
          data
        }));
      }
    });
  }

  // Chat Assistant API
  app.post('/api/chat', async (req, res) => {
    try {
      const { message, conversationId } = req.body;

      if (!message || typeof message !== 'string') {
        return res.status(400).json({ error: 'Message is required' });
      }

      if (!chatService.isConfigured()) {
        return res.status(503).json({ 
          error: 'AI chat service is not configured. Please add OPENROUTER_API_KEY to environment variables.' 
        });
      }

      const result = await chatService.sendMessage(message, conversationId);
      res.json(result);

    } catch (error) {
      console.error('Chat API error:', error);
      res.status(500).json({ 
        error: 'Failed to process chat message',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Chat streaming endpoint
  app.post('/api/chat/stream', async (req, res) => {
    try {
      const { message, conversationId } = req.body;

      if (!message || typeof message !== 'string') {
        return res.status(400).json({ error: 'Message is required' });
      }

      if (!chatService.isConfigured()) {
        return res.status(503).json({ 
          error: 'AI chat service is not configured' 
        });
      }

      res.writeHead(200, {
        'Content-Type': 'text/plain',
        'Transfer-Encoding': 'chunked',
        'Cache-Control': 'no-cache',
        'Connection': 'keep-alive'
      });

      const stream = await chatService.streamMessage(message, conversationId);

      for await (const chunk of stream) {
        res.write(chunk);
      }

      res.end();

    } catch (error) {
      console.error('Chat stream error:', error);
      res.status(500).json({ error: 'Failed to stream chat response' });
    }
  });

  // Markets API
  app.get('/api/markets/snapshot', async (req, res) => {
    try {
      const { kind = 'crypto', symbols } = req.query;

      if (!['crypto', 'stock'].includes(kind as string)) {
        return res.status(400).json({ error: 'Kind must be "crypto" or "stock"' });
      }

      let symbolList: string[];
      if (symbols && typeof symbols === 'string') {
        symbolList = symbols.split(',').map(s => s.trim().toUpperCase());
      } else {
        symbolList = marketService.getDefaultSymbols(kind as 'crypto' | 'stock');
      }

      const data = await marketService.getMarketSnapshot(kind as 'crypto' | 'stock', symbolList);
      res.json(data);

    } catch (error) {
      console.error('Market snapshot error:', error);
      res.status(500).json({ error: 'Failed to fetch market data' });
    }
  });

  // Server-Sent Events for real-time market data
  app.get('/api/markets/stream', (req, res) => {
    const { kind = 'crypto', symbols } = req.query;

    res.writeHead(200, {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      'Connection': 'keep-alive',
      'Access-Control-Allow-Origin': '*'
    });

    const connection = { res, kind, symbols };
    sseConnections.add(connection);

    // Send initial data
    res.write('event: connected\n');
    res.write('data: {"status":"connected"}\n\n');

    req.on('close', () => {
      sseConnections.delete(connection);
    });
  });

  // Periodic market data updates via SSE
  setInterval(async () => {
    for (const connection of Array.from(sseConnections)) {
      try {
        const { res, kind, symbols } = connection;

        let symbolList: string[];
        if (symbols && typeof symbols === 'string') {
          symbolList = symbols.split(',').map((s: string) => s.trim().toUpperCase());
        } else {
          symbolList = marketService.getDefaultSymbols(kind as 'crypto' | 'stock');
        }

        const data = await marketService.getMarketSnapshot(kind as 'crypto' | 'stock', symbolList);

        res.write('event: market_update\n');
        res.write(`data: ${JSON.stringify(data)}\n\n`);

      } catch (error) {
        console.error('SSE update error:', error);
        sseConnections.delete(connection);
      }
    }
  }, 5000); // Update every 5 seconds for crypto, 15 seconds for stocks

  // Market history API
  app.get('/api/markets/history', async (req, res) => {
    try {
      const { kind = 'crypto', symbol, range = '1d' } = req.query;

      if (!symbol || typeof symbol !== 'string') {
        return res.status(400).json({ error: 'Symbol is required' });
      }

      const data = await marketService.getMarketHistory(
        kind as 'crypto' | 'stock', 
        symbol.toUpperCase(), 
        range as string
      );

      res.json(data);

    } catch (error) {
      console.error('Market history error:', error);
      res.status(500).json({ error: 'Failed to fetch market history' });
    }
  });

  // News API
  app.get('/api/news', async (req, res) => {
    try {
      const { category, limit } = req.query;

      const limitNum = limit ? parseInt(limit as string) : 50;
      const categoryStr = category && category !== 'all' ? category as string : undefined;

      const news = await newsService.getNews(categoryStr, limitNum);
      res.json(news);

    } catch (error) {
      console.error('News API error:', error);
      res.status(500).json({ error: 'Failed to fetch news' });
    }
  });

  // Featured news API
  app.get('/api/news/featured', async (req, res) => {
    try {
      const { category } = req.query;
      const categoryStr = category && category !== 'all' ? category as string : undefined;

      const featured = await newsService.getFeaturedNews(categoryStr);
      res.json(featured || null);

    } catch (error) {
      console.error('Featured news error:', error);
      res.status(500).json({ error: 'Failed to fetch featured news' });
    }
  });

  // Calculator APIs
  app.post('/api/calculators/roi', async (req, res) => {
    try {
      const inputs = req.body;
      const result = calculatorService.calculateROI(inputs);
      res.json(result);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Calculation error' });
    }
  });

  app.post('/api/calculators/pl', async (req, res) => {
    try {
      const inputs = req.body;
      const result = calculatorService.calculateProfitLoss(inputs);
      res.json(result);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Calculation error' });
    }
  });

  app.post('/api/calculators/compound', async (req, res) => {
    try {
      const inputs = req.body;
      const result = calculatorService.calculateCompoundInterest(inputs);
      res.json(result);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Calculation error' });
    }
  });

  app.post('/api/calculators/convert', async (req, res) => {
    try {
      const inputs = req.body;

      // Get current market prices for conversion
      const cryptoSymbols = ['BTC', 'ETH', 'SOL', 'ADA'];
      const marketData = await marketService.getMarketSnapshot('crypto', cryptoSymbols);

      const prices: Record<string, number> = { USD: 1 };
      marketData.forEach(item => {
        prices[item.symbol] = item.price;
      });

      const result = calculatorService.convertCurrency(inputs, prices);
      res.json(result);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Conversion error' });
    }
  });

  app.post('/api/calculators/tax', async (req, res) => {
    try {
      const validatedData = taxCalculationSchema.parse(req.body);
      const result = calculatorService.calculateTax(validatedData);
      res.json(result);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : 'Tax calculation error' });
    }
  });

  // Watchlist API
  app.get('/api/watchlist', async (req, res) => {
    try {
      const watchlist = await storage.getWatchlist();
      res.json(watchlist || { symbols: [] });
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch watchlist' });
    }
  });

  app.post('/api/watchlist', async (req, res) => {
    try {
      const { symbols } = req.body;

      if (!Array.isArray(symbols)) {
        return res.status(400).json({ error: 'Symbols must be an array' });
      }

      const existing = await storage.getWatchlist();

      if (existing) {
        const updated = await storage.updateWatchlist(existing.id, symbols);
        res.json(updated);
      } else {
        const created = await storage.saveWatchlist({ symbols });
        res.json(created);
      }
    } catch (error) {
      res.status(500).json({ error: 'Failed to update watchlist' });
    }
  });

  // Health check endpoint
  app.get('/api/health', async (req, res) => {
    try {
      const health = {
        status: 'healthy',
        timestamp: new Date().toISOString(),
        services: {
          market: marketService.getServiceHealth(),
          news: newsService.getServiceHealth(),
          chat: chatService.getServiceHealth(),
          calculator: calculatorService.getServiceHealth()
        },
        connections: {
          websocket: wss.clients.size,
          sse: sseConnections.size
        }
      };

      res.json(health);
    } catch (error) {
      res.status(500).json({ 
        status: 'unhealthy', 
        error: error instanceof Error ? error.message : 'Unknown error' 
      });
    }
  });

  // Provider stats endpoint for debugging and monitoring
  app.get('/api/providers/stats', async (req, res) => {
    try {
      // Access registries through market service
      const cryptoStats = marketService['cryptoRegistry']?.getProviderStats() || [];
      const stockStats = marketService['stockRegistry']?.getProviderStats() || [];

      const providerStats = {
        timestamp: new Date().toISOString(),
        crypto: {
          providers: cryptoStats,
          totalProviders: cryptoStats.length,
          activeProviders: cryptoStats.filter((p: any) => p.circuitBreakerState === 'closed').length
        },
        stock: {
          providers: stockStats,
          totalProviders: stockStats.length,
          activeProviders: stockStats.filter((p: any) => p.circuitBreakerState === 'closed').length
        }
      };

      res.json(providerStats);
    } catch (error) {
      console.error('Provider stats error:', error);
      res.status(500).json({ 
        error: 'Failed to get provider stats',
        message: error instanceof Error ? error.message : 'Unknown error' 
      });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type MarketData, type InsertMarketData, type NewsArticle, type InsertNewsArticle, type ChatConversation, type InsertChatConversation, type Watchlist, type InsertWatchlist } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Market data methods
  getMarketData(symbol: string, kind: "crypto" | "stock"): Promise<MarketData | undefined>;
  getLatestMarketData(kind?: "crypto" | "stock"): Promise<MarketData[]>;
  saveMarketData(data: InsertMarketData): Promise<MarketData>;
  getMarketDataBatch(symbols: string[], kind: "crypto" | "stock"): Promise<MarketData[]>;

  // News methods
  getLatestNews(category?: string, limit?: number): Promise<NewsArticle[]>;
  saveNewsArticle(article: InsertNewsArticle): Promise<NewsArticle>;
  getNewsByUrl(url: string): Promise<NewsArticle | undefined>;

  // Chat methods
  getChatConversation(id: string): Promise<ChatConversation | undefined>;
  saveChatConversation(conversation: InsertChatConversation): Promise<ChatConversation>;
  updateChatConversation(id: string, messages: any[]): Promise<ChatConversation>;

  // Watchlist methods
  getWatchlist(userId?: string): Promise<Watchlist | undefined>;
  saveWatchlist(watchlist: InsertWatchlist): Promise<Watchlist>;
  updateWatchlist(id: string, symbols: string[]): Promise<Watchlist>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private marketData: Map<string, MarketData>;
  private newsArticles: Map<string, NewsArticle>;
  private chatConversations: Map<string, ChatConversation>;
  private watchlists: Map<string, Watchlist>;

  constructor() {
    this.users = new Map();
    this.marketData = new Map();
    this.newsArticles = new Map();
    this.chatConversations = new Map();
    this.watchlists = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getMarketData(symbol: string, kind: "crypto" | "stock"): Promise<MarketData | undefined> {
    const key = `${kind}-${symbol}`;
    return this.marketData.get(key);
  }

  async getLatestMarketData(kind?: "crypto" | "stock"): Promise<MarketData[]> {
    const allData = Array.from(this.marketData.values());
    if (kind) {
      return allData.filter(data => data.kind === kind);
    }
    return allData;
  }

  async saveMarketData(insertData: InsertMarketData): Promise<MarketData> {
    const id = randomUUID();
    const data: MarketData = { 
      ...insertData, 
      id, 
      timestamp: new Date(),
      change24h: insertData.change24h ?? null,
      volume: insertData.volume ?? null,
      marketCap: insertData.marketCap ?? null
    };
    const key = `${data.kind}-${data.symbol}`;
    this.marketData.set(key, data);
    return data;
  }

  async getMarketDataBatch(symbols: string[], kind: "crypto" | "stock"): Promise<MarketData[]> {
    const results: MarketData[] = [];
    for (const symbol of symbols) {
      const data = await this.getMarketData(symbol, kind);
      if (data) results.push(data);
    }
    return results;
  }

  async getLatestNews(category?: string, limit: number = 50): Promise<NewsArticle[]> {
    let articles = Array.from(this.newsArticles.values());

    if (category && category !== "all") {
      articles = articles.filter(article => article.category === category);
    }

    return articles
      .sort((a, b) => new Date(b.publishedAt).getTime() - new Date(a.publishedAt).getTime())
      .slice(0, limit);
  }

  async saveNewsArticle(insertArticle: InsertNewsArticle): Promise<NewsArticle> {
    const id = randomUUID();
    const article: NewsArticle = { 
      ...insertArticle, 
      id, 
      createdAt: new Date(),
      content: insertArticle.content ?? null,
      description: insertArticle.description ?? null,
      imageUrl: insertArticle.imageUrl ?? null
    };
    this.newsArticles.set(article.url, article);
    return article;
  }

  async getNewsByUrl(url: string): Promise<NewsArticle | undefined> {
    return this.newsArticles.get(url);
  }

  async getChatConversation(id: string): Promise<ChatConversation | undefined> {
    return this.chatConversations.get(id);
  }

  async saveChatConversation(insertConversation: InsertChatConversation): Promise<ChatConversation> {
    const id = randomUUID();
    const now = new Date();
    const conversation: ChatConversation = { 
      ...insertConversation, 
      id, 
      createdAt: now,
      updatedAt: now,
      messages: insertConversation.messages ?? []
    };
    this.chatConversations.set(id, conversation);
    return conversation;
  }

  async updateChatConversation(id: string, messages: any[]): Promise<ChatConversation> {
    const existing = this.chatConversations.get(id);
    if (!existing) {
      throw new Error("Conversation not found");
    }

    const updated: ChatConversation = {
      ...existing,
      messages,
      updatedAt: new Date()
    };

    this.chatConversations.set(id, updated);
    return updated;
  }

  async getWatchlist(userId?: string): Promise<Watchlist | undefined> {
    if (!userId) {
      // Return default watchlist
      return Array.from(this.watchlists.values())[0];
    }
    return Array.from(this.watchlists.values()).find(w => w.userId === userId);
  }

  async saveWatchlist(insertWatchlist: InsertWatchlist): Promise<Watchlist> {
    const id = randomUUID();
    const watchlist: Watchlist = { 
      ...insertWatchlist, 
      id, 
      createdAt: new Date(),
      name: insertWatchlist.name ?? "My Watchlist",
      userId: insertWatchlist.userId ?? null,
      symbols: insertWatchlist.symbols ?? []
    };
    this.watchlists.set(id, watchlist);
    return watchlist;
  }

  async updateWatchlist(id: string, symbols: string[]): Promise<Watchlist> {
    const existing = this.watchlists.get(id);
    if (!existing) {
      throw new Error("Watchlist not found");
    }

    const updated: Watchlist = {
      ...existing,
      symbols
    };

    this.watchlists.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

