@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background: linear-gradient(135deg, var(--background) 0%, var(--muted) 100%);
    background-attachment: fixed;
    min-height: 100vh;
    overflow-x: hidden;
  }

  * {
    scrollbar-width: thin;
    scrollbar-color: var(--primary) var(--muted);
  }

  *::-webkit-scrollbar {
    width: 6px;
  }

  *::-webkit-scrollbar-track {
    background: var(--muted);
    border-radius: 3px;
  }

  *::-webkit-scrollbar-thumb {
    background: var(--primary);
    border-radius: 3px;
  }

  *::-webkit-scrollbar-thumb:hover {
    background: var(--accent);
  }
}

@layer components {
  .glass-panel {
    background: var(--card);
    backdrop-filter: blur(25px);
    border: 1px solid var(--border);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.1), 
                0 0 0 1px rgba(255, 255, 255, 0.05) inset;
    transition: all 0.3s ease;
  }

  .glass-panel:hover {
    box-shadow: 0 16px 50px rgba(0, 0, 0, 0.15), 
                0 0 0 1px rgba(255, 255, 255, 0.1) inset,
                0 0 30px rgba(0, 245, 255, 0.1);
    transform: translateY(-1px);
  }

  @media (max-width: 768px) {
    .glass-panel:hover {
      box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1), 
                  0 0 0 1px rgba(255, 255, 255, 0.05) inset;
      transform: none;
    }
  }

  .neon-glow {
    box-shadow: 0 0 25px rgba(0, 245, 255, 0.4), 
                0 0 50px rgba(0, 245, 255, 0.2);
    transition: all 0.3s ease;
  }

  .neon-glow:hover {
    box-shadow: 0 0 35px rgba(0, 245, 255, 0.6),
                0 0 70px rgba(0, 245, 255, 0.3);
    transform: scale(1.02);
  }

  /* Mobile optimizations - reduce heavy effects */
  @media (max-width: 768px) {
    .neon-glow {
      box-shadow: 0 2px 8px rgba(0, 245, 255, 0.2);
    }

    .neon-glow:hover {
      box-shadow: 0 4px 12px rgba(0, 245, 255, 0.3);
      transform: none;
    }
  }

  .gradient-text {
    background: linear-gradient(45deg, hsl(188 100% 50%), hsl(262 83% 58%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .chart-container {
    height: 400px;
    background: var(--card);
    border: 1px solid var(--border);
    border-radius: var(--radius);
    position: relative;
    overflow: hidden;
  }

  .sparkline {
    stroke: var(--primary);
    stroke-width: 2;
    fill: none;
  }

  .market-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .market-card:hover {
    transform: translateY(-4px) scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15),
                0 0 30px rgba(0, 245, 255, 0.2);
  }

  @media (max-width: 768px) {
    .market-card:hover {
      transform: translateY(-2px);
      box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
    }
  }

  .pulse-dot {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .streaming-indicator {
    background: linear-gradient(90deg, transparent, rgba(0, 245, 255, 0.3), transparent);
    animation: shimmer 2s infinite;
  }

  .news-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .news-card:hover {
    transform: translateY(-3px) scale(1.01);
    box-shadow: 0 15px 50px rgba(0, 0, 0, 0.2),
                0 0 25px rgba(0, 245, 255, 0.15);
  }

  @media (max-width: 768px) {
    .news-card:hover {
      transform: translateY(-1px);
      box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1);
    }
  }

  .calculator-input {
    background: var(--input);
    border: 1px solid var(--border);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
  }

  .calculator-input:focus {
    outline: none;
    border-color: var(--primary);
    box-shadow: 0 0 0 3px rgba(0, 245, 255, 0.2),
                0 0 20px rgba(0, 245, 255, 0.1);
    transform: translateY(-1px);
  }

  .chat-message {
    animation: slideIn 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .loading-dots {
    animation: loadingPulse 1.5s infinite;
  }

  .nav-tab {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
  }

  .nav-tab::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 245, 255, 0.1), transparent);
    transition: left 0.5s ease;
  }

  .nav-tab:hover::before {
    left: 100%;
  }
}

@layer utilities {
  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .fade-in {
    opacity: 0;
    animation: fadeIn 0.5s ease-in-out forwards;
  }

  /* Premium Input Field Styles */
  input:focus {
    outline: none;
    transform: translateY(-1px);
  }

  @media (max-width: 768px) {
    input:focus {
      transform: none;
    }

    .calculator-input:focus {
      transform: none;
    }
  }

  input::placeholder {
    transition: all 0.3s ease-in-out;
  }

  input:focus::placeholder {
    opacity: 0.7;
    transform: translateY(-2px);
  }

  @keyframes fadeIn {
    0% {
      opacity: 0;
      transform: translateY(10px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes glow {
    0% {
      box-shadow: 0 0 25px rgba(0, 245, 255, 0.4);
    }
    100% {
      box-shadow: 0 0 50px rgba(0, 245, 255, 0.8),
                  0 0 80px rgba(0, 245, 255, 0.4);
    }
  }

  /* Reduce motion for mobile users and accessibility */
  @media (max-width: 768px), (prefers-reduced-motion: reduce) {
    .animate-glow,
    .animate-float,
    .pulse-dot {
      animation: none;
    }

    .nav-tab::before {
      display: none;
    }

    * {
      transition-duration: 0.1s !important;
    }
  }

  @keyframes slideIn {
    0% {
      opacity: 0;
      transform: translateY(20px) scale(0.95);
    }
    100% {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  @keyframes loadingPulse {
    0%, 20% {
      opacity: 0.3;
      transform: scale(0.8);
    }
    50% {
      opacity: 1;
      transform: scale(1.1);
    }
    80%, 100% {
      opacity: 0.3;
      transform: scale(0.8);
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  @keyframes shimmer {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }
}

