import { calculateHoldingPeriod, isLongTermHolding, calculateTaxRate } from '../utils/formatters.js';
import { type CalculatorInput, type TaxCalculation } from '@shared/schema';

export class CalculatorService {

  calculateROI(inputs: Record<string, number>): any {
    const { initialInvestment, currentValue, purchaseDate } = inputs;

    if (!initialInvestment || !currentValue) {
      throw new Error('Initial investment and current value are required');
    }

    const absoluteGain = currentValue - initialInvestment;
    const percentageGain = (absoluteGain / initialInvestment) * 100;

    let annualizedReturn = 0;
    let holdingPeriod = 0;

    if (purchaseDate) {
      holdingPeriod = calculateHoldingPeriod(new Date(purchaseDate).toISOString());
      if (holdingPeriod > 0) {
        const years = holdingPeriod / 365;
        annualizedReturn = (Math.pow(currentValue / initialInvestment, 1 / years) - 1) * 100;
      }
    }

    return {
      initialInvestment,
      currentValue,
      absoluteGain,
      percentageGain,
      annualizedReturn,
      holdingPeriod,
      isProfit: absoluteGain > 0
    };
  }

  calculateProfitLoss(inputs: Record<string, number>): any {
    const { entryPrice, exitPrice, quantity, fees = 0, positionType = 1 } = inputs; // 1 = long, -1 = short

    if (!entryPrice || !exitPrice || !quantity) {
      throw new Error('Entry price, exit price, and quantity are required');
    }

    const direction = positionType >= 0 ? 1 : -1;
    const grossPnL = direction * (exitPrice - entryPrice) * quantity;
    const totalFees = fees * 2; // Entry + exit fees
    const netPnL = grossPnL - totalFees;
    const roi = (netPnL / (entryPrice * quantity)) * 100;

    return {
      entryPrice,
      exitPrice,
      quantity,
      grossPnL,
      totalFees,
      netPnL,
      roi,
      positionType: direction > 0 ? 'Long' : 'Short',
      isProfit: netPnL > 0
    };
  }

  calculateCompoundInterest(inputs: Record<string, any>): any {
    const { 
      principal, 
      annualRate, 
      timePeriod, 
      timeUnit = 'years', 
      compoundFrequency = 12, 
      monthlyContribution = 0 
    } = inputs;

    if (!principal || !annualRate || !timePeriod) {
      throw new Error('Principal, annual rate, and time period are required');
    }

    const rate = annualRate / 100;
    const years = timeUnit === 'years' ? timePeriod : timePeriod / 12;
    const periodsPerYear = compoundFrequency;
    const totalPeriods = years * periodsPerYear;

    // Compound interest formula with regular contributions
    const futureValue = principal * Math.pow(1 + rate / periodsPerYear, totalPeriods) +
      monthlyContribution * (Math.pow(1 + rate / periodsPerYear, totalPeriods) - 1) / (rate / periodsPerYear);

    const totalContributions = principal + (monthlyContribution * 12 * years);
    const interestEarned = futureValue - totalContributions;

    // Year-by-year breakdown
    const yearlyBreakdown = [];
    for (let year = 1; year <= Math.min(years, 10); year++) {
      const periods = year * periodsPerYear;
      const yearValue = principal * Math.pow(1 + rate / periodsPerYear, periods) +
        monthlyContribution * (Math.pow(1 + rate / periodsPerYear, periods) - 1) / (rate / periodsPerYear);

      yearlyBreakdown.push({
        year,
        value: Math.round(yearValue * 100) / 100,
        contributions: principal + (monthlyContribution * 12 * year),
        interest: yearValue - (principal + (monthlyContribution * 12 * year))
      });
    }

    return {
      principal,
      annualRate,
      years,
      monthlyContribution,
      compoundFrequency,
      futureValue: Math.round(futureValue * 100) / 100,
      totalContributions,
      interestEarned: Math.round(interestEarned * 100) / 100,
      effectiveRate: ((futureValue / totalContributions - 1) * 100),
      yearlyBreakdown
    };
  }

  convertCurrency(inputs: Record<string, any>, marketPrices: Record<string, number>): any {
    const { fromCurrency, toCurrency, amount } = inputs;

    if (!fromCurrency || !toCurrency || !amount) {
      throw new Error('From currency, to currency, and amount are required');
    }

    let fromRate = 1;
    let toRate = 1;

    // Get rates from market prices (assuming USD as base)
    if (fromCurrency !== 'USD' && marketPrices[fromCurrency]) {
      fromRate = marketPrices[fromCurrency];
    }

    if (toCurrency !== 'USD' && marketPrices[toCurrency]) {
      toRate = marketPrices[toCurrency];
    }

    // Convert via USD
    const usdAmount = fromCurrency === 'USD' ? amount : amount * fromRate;
    const convertedAmount = toCurrency === 'USD' ? usdAmount : usdAmount / toRate;

    const exchangeRate = convertedAmount / amount;

    return {
      fromCurrency,
      toCurrency,
      amount,
      convertedAmount: Math.round(convertedAmount * 100000) / 100000,
      exchangeRate: Math.round(exchangeRate * 100000) / 100000,
      rates: {
        [fromCurrency]: fromRate,
        [toCurrency]: toRate
      }
    };
  }

  calculateTax(calculation: TaxCalculation): any {
    const {
      quantity,
      purchasePrice,
      salePrice,
      purchaseDate,
      saleDate,
      fees = 0,
      region = 'US',
      taxBracket
    } = calculation;

    if (!quantity || !purchasePrice || !salePrice || !purchaseDate) {
      throw new Error('Quantity, purchase price, sale price, and purchase date are required');
    }

    const totalCost = quantity * purchasePrice + fees;
    const totalSale = quantity * salePrice - fees;
    const capitalGains = totalSale - totalCost;

    const holdingDays = saleDate 
      ? calculateHoldingPeriod(purchaseDate, saleDate)
      : 0;

    const isLongTerm = isLongTermHolding(holdingDays);

    // Calculate tax rate based on holding period and income
    const taxRate = calculateTaxRate(taxBracket, isLongTerm, region);
    const taxOwed = Math.max(0, capitalGains * (taxRate / 100));
    const netProfit = capitalGains - taxOwed;
    const effectiveRate = capitalGains > 0 ? (taxOwed / capitalGains) * 100 : 0;

    // Scenario comparison
    const shortTermTaxRate = calculateTaxRate(taxBracket, false, region);
    const shortTermTax = Math.max(0, capitalGains * (shortTermTaxRate / 100));
    const shortTermNet = capitalGains - shortTermTax;
    const taxSavings = shortTermTax - taxOwed;

    return {
      purchasePrice,
      salePrice,
      quantity,
      totalCost,
      totalSale,
      fees,
      capitalGains,
      holdingDays,
      isLongTerm,
      taxRate,
      taxOwed: Math.round(taxOwed * 100) / 100,
      netProfit: Math.round(netProfit * 100) / 100,
      effectiveRate: Math.round(effectiveRate * 100) / 100,
      scenarios: {
        current: {
          type: isLongTerm ? 'Long-term' : 'Short-term',
          taxRate,
          taxOwed: Math.round(taxOwed * 100) / 100,
          netProfit: Math.round(netProfit * 100) / 100
        },
        shortTerm: {
          type: 'Short-term',
          taxRate: shortTermTaxRate,
          taxOwed: Math.round(shortTermTax * 100) / 100,
          netProfit: Math.round(shortTermNet * 100) / 100
        },
        savings: Math.round(taxSavings * 100) / 100
      }
    };
  }

  getServiceHealth() {
    return {
      supportedCalculations: ['roi', 'pl', 'compound', 'conversion', 'tax'],
      regions: ['US', 'UK', 'CA', 'AU'],
      currencies: ['USD', 'EUR', 'GBP', 'BTC', 'ETH', 'SOL', 'ADA']
    };
  }
}

server/services/chatService.ts.content:
import { storage } from '../storage.js';
import { type ChatMessage } from '@shared/schema';

export class ChatService {
  private apiKey: string | null = null;
  private readonly apiUrl = 'https://openrouter.ai/api/v1/chat/completions';
  private readonly model = 'deepseek/deepseek-chat-v3.1:free';
  private readonly systemPrompt = `You are FinChat, an expert AI financial assistant specializing in cryptocurrency, stock markets, and personal finance.

Your role is to:
- Provide insights about financial markets, investments, and trading strategies
- Analyze market trends and deliver actionable analysis  
- Help users understand financial concepts and calculations
- Offer portfolio management and risk assessment guidance
- Explain tax implications and optimization strategies
- Discuss economic indicators and their market impact

Guidelines:
- Provide clear, confident analysis and recommendations
- Present objective perspectives on investment opportunities  
- Explain both opportunities and risks clearly
- Use current market data to inform your responses
- Give specific, actionable insights when possible

Current market context: You have access to real-time crypto and stock market data, news feeds, and financial calculators. Reference this data to provide accurate and timely insights.`;

  constructor() {
    if (process.env.OPENROUTER_API_KEY) {
      this.apiKey = process.env.OPENROUTER_API_KEY;
    }
  }

  async sendMessage(message: string, conversationId?: string): Promise<{ response: string; conversationId: string }> {
    if (!this.apiKey) {
      throw new Error('OpenRouter API key not configured. Please set OPENROUTER_API_KEY environment variable.');
    }

    let conversation;
    let messages: ChatMessage[] = [];

    // Load existing conversation or create new one
    if (conversationId) {
      conversation = await storage.getChatConversation(conversationId);
      if (conversation) {
        messages = Array.isArray(conversation.messages) ? conversation.messages : [];
      }
    }

    // Add user message
    const userMessage: ChatMessage = {
      role: 'user',
      content: message,
      timestamp: new Date().toISOString()
    };
    messages.push(userMessage);

    try {
      // Using DeepSeek model via OpenRouter for reliable chat functionality
      const response = await fetch(this.apiUrl, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json',
          'HTTP-Referer': 'https://finchat.app',
          'X-Title': 'FinChat'
        },
        body: JSON.stringify({
          model: this.model,
          messages: [
            { role: 'system', content: this.systemPrompt },
            ...messages.map(msg => ({ role: msg.role, content: msg.content }))
          ],
          max_tokens: 2000,
          stream: false
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(`OpenRouter API error: ${response.status} ${response.statusText} - ${JSON.stringify(errorData)}`);
      }

      const completion = await response.json();
      const assistantResponse = completion.choices?.[0]?.message?.content || 'I apologize, but I was unable to generate a response. Please try again.';

      // Add assistant message
      const assistantMessage: ChatMessage = {
        role: 'assistant',
        content: assistantResponse,
        timestamp: new Date().toISOString()
      };
      messages.push(assistantMessage);

      // Save conversation
      if (conversation) {
        conversation = await storage.updateChatConversation(conversationId!, messages);
      } else {
        conversation = await storage.saveChatConversation({ messages });
      }

      return {
        response: assistantResponse,
        conversationId: conversation.id
      };

    } catch (error) {
      console.error('OpenRouter API error:', error);

      // Provide helpful error message
      let errorMessage = 'I apologize, but I encountered an error while processing your request. ';

      if (error instanceof Error) {
        if (error.message.includes('rate limit')) {
          errorMessage += 'I\'m currently experiencing high demand. Please try again in a moment.';
        } else if (error.message.includes('quota')) {
          errorMessage += 'The AI service quota has been exceeded. Please try again later.';
        } else {
          errorMessage += 'Please try again or rephrase your question.';
        }
      }

      // Add error message to conversation
      const errorResponse: ChatMessage = {
        role: 'assistant',
        content: errorMessage,
        timestamp: new Date().toISOString()
      };
      messages.push(errorResponse);

      // Save conversation with error
      if (conversation) {
        conversation = await storage.updateChatConversation(conversationId!, messages);
      } else {
        conversation = await storage.saveChatConversation({ messages });
      }

      return {
        response: errorMessage,
        conversationId: conversation.id
      };
    }
  }

  async streamMessage(message: string, conversationId?: string): Promise<AsyncIterableIterator<string>> {
    if (!this.apiKey) {
      throw new Error('OpenRouter API key not configured');
    }

    let conversation;
    let messages: ChatMessage[] = [];

    if (conversationId) {
      conversation = await storage.getChatConversation(conversationId);
      if (conversation) {
        messages = Array.isArray(conversation.messages) ? conversation.messages : [];
      }
    }

    const userMessage: ChatMessage = {
      role: 'user',
      content: message,
      timestamp: new Date().toISOString()
    };
    messages.push(userMessage);

    // Using DeepSeek model via OpenRouter for reliable streaming chat functionality
    const response = await fetch(this.apiUrl, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${this.apiKey}`,
        'Content-Type': 'application/json',
        'HTTP-Referer': 'https://finchat.app',
        'X-Title': 'FinChat'
      },
      body: JSON.stringify({
        model: this.model,
        messages: [
          { role: 'system', content: this.systemPrompt },
          ...messages.map(msg => ({ role: msg.role, content: msg.content }))
        ],
        max_tokens: 2000,
        stream: true
      })
    });

    if (!response.ok) {
      throw new Error(`OpenRouter API error: ${response.status} ${response.statusText}`);
    }

    return this.createStreamIterator(response, messages, conversation?.id);
  }

  private async *createStreamIterator(
    response: Response,
    messages: ChatMessage[],
    conversationId?: string
  ): AsyncIterableIterator<string> {
    let fullResponse = '';

    try {
      const reader = response.body?.getReader();
      if (!reader) {
        throw new Error('No response body');
      }

      const decoder = new TextDecoder();

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') {
              break;
            }

            try {
              const parsed = JSON.parse(data);
              const content = parsed.choices?.[0]?.delta?.content || '';
              if (content) {
                fullResponse += content;
                yield content;
              }
            } catch (e) {
              // Ignore JSON parsing errors for malformed chunks
            }
          }
        }
      }

      // Save complete response
      const assistantMessage: ChatMessage = {
        role: 'assistant',
        content: fullResponse,
        timestamp: new Date().toISOString()
      };
      messages.push(assistantMessage);

      if (conversationId) {
        await storage.updateChatConversation(conversationId, messages);
      } else {
        await storage.saveChatConversation({ messages });
      }

    } catch (error) {
      console.error('Stream error:', error);
      yield '\n\n[Error: Stream interrupted. Please try again.]';
    }
  }

  async getConversation(conversationId: string): Promise<ChatMessage[]> {
    const conversation = await storage.getChatConversation(conversationId);
    return Array.isArray(conversation?.messages) ? conversation.messages : [];
  }

  isConfigured(): boolean {
    return this.apiKey !== null;
  }

  getServiceHealth() {
    return {
      configured: this.isConfigured(),
      model: this.model,
      systemPromptLength: this.systemPrompt.length
    };
  }
}

server/services/marketService.ts.content:
import { CryptoProviderRegistry } from '../providers/cryptoProviders.js';
import { StockProviderRegistry } from '../providers/stockProviders.js';
import { marketCache } from '../utils/cache.js';
import { storage } from '../storage.js';
import { type UnifiedMarketData } from '@shared/schema';

export class MarketService {
  private cryptoRegistry: CryptoProviderRegistry;
  private stockRegistry: StockProviderRegistry;
  private activeStreams: Map<string, any> = new Map();

  constructor() {
    this.cryptoRegistry = new CryptoProviderRegistry();
    this.stockRegistry = new StockProviderRegistry();
  }

  async getMarketSnapshot(kind: 'crypto' | 'stock', symbols: string[]): Promise<UnifiedMarketData[]> {
    const cacheKey = `${kind}-${symbols.join(',')}-snapshot`;

    // Check cache first
    const cached = marketCache.get(cacheKey);
    if (cached && Array.isArray(cached)) {
      return cached;
    }

    let data: UnifiedMarketData[] = [];

    try {
      if (kind === 'crypto') {
        data = await this.cryptoRegistry.fetchSnapshot(symbols);
      } else {
        data = await this.stockRegistry.fetchSnapshot(symbols);
      }

      // Cache with appropriate TTL
      const ttl = kind === 'crypto' ? 15000 : 30000; // 15s for crypto, 30s for stocks
      marketCache.set(cacheKey, data, ttl);

      // Store in persistent storage
      await this.saveMarketData(data);

    } catch (error) {
      console.error(`Market snapshot error for ${kind}:`, error);

      // Try to return cached data from storage as fallback
      const fallbackData = await storage.getMarketDataBatch(symbols, kind);
      if (fallbackData.length > 0) {
        data = fallbackData.map(item => ({
          symbol: item.symbol,
          name: item.name,
          price: parseFloat(item.price),
          change24h: item.change24h ? parseFloat(item.change24h) : undefined,
          volume: item.volume ? parseFloat(item.volume) : undefined,
          marketCap: item.marketCap ? parseFloat(item.marketCap) : undefined,
          timestamp: item.timestamp.toISOString(),
          source: item.source,
          kind: item.kind as 'crypto' | 'stock'
        }));
      }
    }

    return data;
  }

  async getMarketHistory(kind: 'crypto' | 'stock', symbol: string, range: string): Promise<any[]> {
    const cacheKey = `${kind}-${symbol}-${range}-history`;

    // Check cache first
    const cached = marketCache.get(cacheKey);
    if (cached && Array.isArray(cached)) {
      return cached;
    }

    let data: any[] = [];

    try {
      if (kind === 'crypto') {
        data = await this.cryptoRegistry.fetchHistory(symbol, range);
      } else {
        data = await this.stockRegistry.fetchHistory(symbol, range);
      }

      // Cache for 5 minutes
      marketCache.set(cacheKey, data, 5 * 60 * 1000);

    } catch (error) {
      console.error(`Market history error for ${kind} ${symbol}:`, error);
    }

    return data;
  }

  private async saveMarketData(data: UnifiedMarketData[]): Promise<void> {
    try {
      for (const item of data) {
        await storage.saveMarketData({
          symbol: item.symbol,
          name: item.name,
          price: item.price.toString(),
          change24h: item.change24h?.toString(),
          volume: item.volume?.toString(),
          marketCap: item.marketCap?.toString(),
          source: item.source,
          kind: item.kind
        });
      }
    } catch (error) {
      console.error('Error saving market data:', error);
    }
  }

  async initializeLiveStreams(): Promise<void> {
    try {
      // Initialize crypto live streams
      const cryptoSymbols = ['BTC', 'ETH', 'SOL', 'ADA', 'DOT', 'MATIC', 'AVAX', 'LINK'];
      const connections = await this.cryptoRegistry.connectLiveStreams(cryptoSymbols);

      connections.forEach((ws, index) => {
        this.activeStreams.set(`crypto-stream-${index}`, ws);
      });

      console.log(`Initialized ${connections.length} crypto live streams`);
    } catch (error) {
      console.error('Error initializing live streams:', error);
    }
  }

  getServiceHealth() {
    return {
      cryptoProviders: this.cryptoRegistry.getProviderStats(),
      stockProviders: this.stockRegistry.getProviderStats(),
      cacheStats: marketCache.getStats(),
      activeStreams: this.activeStreams.size
    };
  }

  // Default symbols for each market type
  getDefaultSymbols(kind: 'crypto' | 'stock'): string[] {
    if (kind === 'crypto') {
      return ['BTC', 'ETH', 'SOL', 'ADA', 'DOT', 'MATIC', 'AVAX', 'LINK', 'UNI', 'LTC'];
    } else {
      return ['AAPL', 'GOOGL', 'MSFT', 'AMZN', 'TSLA', 'META', 'NVDA', 'NFLX', 'SPY', 'QQQ'];
    }
  }
}

server/services/newsService.ts.content:
import { NewsProviderRegistry } from '../providers/newsProviders.js';
import { newsCache } from '../utils/cache.js';
import { storage } from '../storage.js';
import { type NewsArticleData } from '@shared/schema';

export class NewsService {
  private newsRegistry: NewsProviderRegistry;
  private lastFetchTime: number = 0;
  private readonly fetchInterval = 10 * 60 * 1000; // 10 minutes

  constructor() {
    this.newsRegistry = new NewsProviderRegistry();
    this.startPeriodicFetch();
  }

  async getNews(category?: string, limit: number = 50): Promise<NewsArticleData[]> {
    const cacheKey = `news-${category || 'all'}-${limit}`;

    // Check cache first
    const cached = newsCache.get(cacheKey);
    if (cached && Array.isArray(cached)) {
      return cached;
    }

    let news: NewsArticleData[] = [];

    try {
      // Try to fetch from providers first
      news = await this.newsRegistry.fetchAllNews(category);

      if (news.length > 0) {
        // Save to storage
        await this.saveNewsArticles(news);

        // Cache for 10 minutes
        newsCache.set(cacheKey, news.slice(0, limit), this.fetchInterval);
      } else {
        // Fallback to storage
        const fallbackNews = await storage.getLatestNews(category, limit);
        news = this.transformStorageNewsToAPIFormat(fallbackNews);
      }

    } catch (error) {
      console.error('Error fetching news:', error);

      // Fallback to storage
      const fallbackNews = await storage.getLatestNews(category, limit);
      news = this.transformStorageNewsToAPIFormat(fallbackNews);
    }

    return news.slice(0, limit);
  }

  private transformStorageNewsToAPIFormat(storageNews: any[]): NewsArticleData[] {
    return storageNews.map(article => ({
      title: article.title,
      description: article.description || undefined,
      content: article.content || undefined,
      url: article.url,
      imageUrl: article.imageUrl || undefined,
      source: article.source,
      category: ['crypto', 'stocks', 'general'].includes(article.category) 
        ? article.category as 'crypto' | 'stocks' | 'general'
        : 'general',
      publishedAt: article.publishedAt instanceof Date 
        ? article.publishedAt.toISOString()
        : article.publishedAt
    }));
  }

  private async saveNewsArticles(articles: NewsArticleData[]): Promise<void> {
    try {
      for (const article of articles) {
        // Check if article already exists
        const existing = await storage.getNewsByUrl(article.url);
        if (!existing) {
          await storage.saveNewsArticle({
            title: article.title,
            description: article.description,
            content: undefined, // We don't fetch full content from RSS
            url: article.url,
            imageUrl: article.imageUrl,
            source: article.source,
            category: article.category,
            publishedAt: new Date(article.publishedAt)
          });
        }
      }
    } catch (error) {
      console.error('Error saving news articles:', error);
    }
  }

  private startPeriodicFetch(): void {
    // Initial fetch
    this.fetchAndCacheNews();

    // Set up periodic fetching
    setInterval(() => {
      this.fetchAndCacheNews();
    }, this.fetchInterval);
  }

  private async fetchAndCacheNews(): Promise<void> {
    try {
      console.log('Fetching news from all sources...');

      // Fetch all categories
      const allNews = await this.newsRegistry.fetchAllNews();
      const cryptoNews = allNews.filter(n => n.category === 'crypto');
      const stockNews = allNews.filter(n => n.category === 'stocks');

      // Save to storage
      await this.saveNewsArticles(allNews);

      // Update cache
      newsCache.set('news-all-50', allNews.slice(0, 50), this.fetchInterval);
      newsCache.set('news-crypto-50', cryptoNews.slice(0, 50), this.fetchInterval);
      newsCache.set('news-stocks-50', stockNews.slice(0, 50), this.fetchInterval);

      this.lastFetchTime = Date.now();

      console.log(`Fetched ${allNews.length} news articles (${cryptoNews.length} crypto, ${stockNews.length} stocks)`);

    } catch (error) {
      console.error('Error in periodic news fetch:', error);
    }
  }

  getFeaturedNews(category?: string): Promise<NewsArticleData | undefined> {
    return this.getNews(category, 1).then(news => news[0]);
  }

  getServiceHealth() {
    return {
      providers: this.newsRegistry.getProviderStats(),
      cacheStats: newsCache.getStats(),
      lastFetchTime: this.lastFetchTime,
      nextFetchIn: Math.max(0, this.fetchInterval - (Date.now() - this.lastFetchTime))
    };
  }

  clearCache(): void {
    this.newsRegistry.clearCache();
    newsCache.clear();
  }
}

server/utils/cache.ts.content:
class LRUCache<T> {
  private capacity: number;
  private cache: Map<string, { value: T; expiry: number }>;

  constructor(capacity: number = 1000) {
    this.capacity = capacity;
    this.cache = new Map();
  }

  get(key: string): T | undefined {
    const item = this.cache.get(key);
    if (!item) return undefined;

    if (Date.now() > item.expiry) {
      this.cache.delete(key);
      return undefined;
    }

    // Move to end (most recently used)
    this.cache.delete(key);
    this.cache.set(key, item);
    return item.value;
  }

  set(key: string, value: T, ttlMs: number): void {
    if (this.cache.has(key)) {
      this.cache.delete(key);
    } else if (this.cache.size >= this.capacity) {
      // Remove least recently used (first item)
      const firstKey = this.cache.keys().next().value;
      if (firstKey !== undefined) {
        this.cache.delete(firstKey);
      }
    }

    this.cache.set(key, {
      value,
      expiry: Date.now() + ttlMs
    });
  }

  has(key: string): boolean {
    const item = this.cache.get(key);
    if (!item) return false;

    if (Date.now() > item.expiry) {
      this.cache.delete(key);
      return false;
    }

    return true;
  }

  delete(key: string): boolean {
    return this.cache.delete(key);
  }

  clear(): void {
    this.cache.clear();
  }

  size(): number {
    return this.cache.size;
  }

  getStats() {
    return {
      size: this.cache.size,
      capacity: this.capacity,
      hitRate: 0 // TODO: Implement hit rate tracking
    };
  }
}

// Cache instances for different data types
export const marketCache = new LRUCache(500); // 15s TTL for crypto, 30s for stocks
export const newsCache = new LRUCache(200); // 10-15 minutes TTL
export const chartCache = new LRUCache(100); // 5 minutes TTL for chart data

export { LRUCache };

